import React, { Component } from "react";
import Button from "react-bootstrap/Button";
import Container from "react-bootstrap/Container";
import Form from "react-bootstrap/Form";
import Nav from "react-bootstrap/Nav";
import Navbar from "react-bootstrap/Navbar";
import { NavLink } from "react-router-dom";

export class Navigation extends Component {
  render() {
    return (
      <>
        <Navbar className="nav-custom"  bg="success" expand="lg">
          <Container fluid>
            <Navbar.Brand href="#">İCO</Navbar.Brand>
            <Navbar.Toggle aria-controls="navbarScroll" />
            <Navbar.Collapse id="navbarScroll">
              <Nav
                className="me-auto my-2 my-lg-5"
                style={{ maxHeight: "100px" }}
                navbarScroll
              >
                <NavLink className="text-white d-inline p-2 text-decoration-none" to="/" >Home</NavLink>
                <NavLink className="text-white d-inline p-2 text-decoration-none" to="/department" >Department</NavLink>
                <NavLink className="text-white d-inline p-2 text-decoration-none" to="/employee" >Employee</NavLink>
             
              </Nav>
           
            </Navbar.Collapse>
          </Container>
        </Navbar>
      </>
    );
  }
}
