import React, { Component } from "react";
import Table from "react-bootstrap/Table";
import { AddDepartmentModal } from "./AddDepartmentModal";
import { Button, ButtonToolbar } from "react-bootstrap";
export class Department extends Component {
  constructor(props) {
    super(props);
    this.state = {
      deps: [],
      addModalShow: false,
      editModalShow:false
    };
  }

  componentDidMount() {
    this.refleshlist();
  }
  //anlık veri güncelleme 
  componentDidUpdate(){
    this.refleshlist();
  }

  refleshlist() {
    fetch("https://localhost:44323/api/department/")
      .then((response) => response.json())
      .then((data) => {
        this.setState({
          deps: data,
        });
      });

    // https://localhost:44323/api/department
    //DAMI DATA
    // this.setState({
    //     deps:[
    //         {"DepartmentID":1,"DepartmentName":"IT"},
    //         {"DepartmentID":2,"DepartmentName":"SUPPORT"},
    //     ]
    // })
  }

  render() {
    const { deps } = this.state;
    let addModalClose=()=> this.setState({addModalShow:false})
    return (
      <>
        <Table className="mt-4" striped bordered hover size="sm">
          <thead>
            <tr>
              <th>DepartmentID</th>
              <th>DepartmentName</th>
              <th>Option</th>
            </tr>
          </thead>
          <tbody>
            {deps.map((dep) => (
              <tr key={dep.DepartmentID}>
                <td>{dep.DepartmentID}</td>
                <td>{dep.DepartmentName}</td>
                <td>

                </td>
              </tr>
            ))}
          </tbody>
        </Table>

        <ButtonToolbar>
          <Button
            onClick={() => 
              this.setState({
                addModalShow: true,
            })} >

            Add
          </Button>
        </ButtonToolbar>
        <AddDepartmentModal show={this.state.addModalShow} onHide={addModalClose} />
      </>
    );
  }
}
