import React, { Component } from "react";
import { Button, Modal, Row, Col, Form } from "react-bootstrap";
import SnackBar from "@material-ui/core/SnackBar";
import IconButton from "@material-ui/core/IconButton";

export class AddEmpModal extends Component {
  constructor(props) {
    super(props);
    this.state = {
      snackbarOpen: false,
      snackbarMSG: "",
    };

    this.handleSubmit = this.handleSubmit.bind(this);
  }

  snackbarClose = () => {
    this.setState({
      snackbarOpen: false,
    });
  };

  handleSubmit(e) {
    e.preventDefault();

    fetch("https://localhost:44323/api/employee", {
      method: "POST",
      headers: {
        Accept: "application/json",
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        DepartmentID: null,
        DepartmentName: e.target.DepartmentName.value,
      }),
    })
      .then((res) => res.json())
      .then(
        (result) => {
          //   alert(result);
          this.setState({
            snackbarOpen: true,
            snackbarMSG: result,
          });
        },
        (error) => {
          //   alert("Başarısız")
          this.setState({
            snackbarOpen: true,
            snackbarMSG: "Başarısız",
          });
        }
      );

    // alert(e.target.DepartmentName.value)
  }

  render() {
    return <div>AddEmpModal</div>;
  }
}
