import React, { Component } from "react";
import Table from "react-bootstrap/Table";
import { AddEmpModal } from "./AddEmpModal";
import { Button, ButtonToolbar } from "react-bootstrap";
import { EditEmpModal } from "./EditEmpModal";

export class Employee extends Component {
  constructor(props) {
    super(props);
    this.state = {
      emps: [],
      addModalShow: false,
      editModalShow: false,
    };
  }

  componentDidMount() {
    this.refleshlist();
  }
  //anlık veri güncelleme
  componentDidUpdate() {
    this.refleshlist();
  }

  refleshlist() {
    fetch("https://localhost:44323/api/employee/")
      .then((response) => response.json())
      .then((data) => {
        this.setState({
          emps: data,
        });
      });
  }

  deleteEmp(empid) {
    if (window.confirm("Eminmisiniz?")) {
      fetch("https://localhost:44323/api/employee/" + empid, {
        method: "DELETE",
        headers: {
          Accept: "application/json",
          "Content-Type": "application/json",
        },
      });
    }
  }

  render() {
    const { emps, empid, empName, depmt, doj, mailid } = this.state;
    let addModalClose = () => this.setState({ addModalShow: false });
    let editModalClose = () => this.setState({ editModalShow: false });
    return (
      <>
        <Table className="mt-4" striped bordered hover size="sm">
          <thead>
            <tr>
              <th>EmployeeID</th>
              <th>EmployeeName</th>
              <th>Department</th>
              <th>Mail</th>
              <th>Date</th>
              <th>Option</th>
            </tr>
          </thead>
          <tbody>
            {emps.map((emp) => (
              <tr key={emp.DepartmentID}>
                <td>{emp.EmployeeID}</td>
                <td>{emp.EmployeeName}</td>
                <td>{emp.Department}</td>
                <td>{emp.MailId}</td>
                <td>{emp.DOJ}</td>

                <td>
                  <ButtonToolbar>
                    <Button
                      variant="success"
                      onClick={() =>
                        this.setState({
                          editModalShow: true,
                          empid: emp.EmployeeID,
                          empName: emp.EmployeeName,
                        })
                      }
                    >
                      Edit
                    </Button>

                    <Button
                      className="ml-4"
                      variant="danger"
                      onClick={() => this.deleteEmp(emp.EmployeeID)}
                    >
                      DELETE
                    </Button>

                    <EditEmpModal
                      show={this.state.editModalShow}
                      onHide={editModalClose}
                      empid={empid}
                      empName={empName}
                      depmt={emp.Department}
                      doj={emp.DOJ}
                      mailid={emp.MailId}
                    />
                  </ButtonToolbar>
                </td>
              </tr>
            ))}
          </tbody>
        </Table>

        <ButtonToolbar>
          <Button
            onClick={() =>
              this.setState({
                addModalShow: true,
              })
            }
          >
            Add
          </Button>
        </ButtonToolbar>
        <AddEmpModal show={this.state.addModalShow} onHide={addModalClose} />
      </>
    );
  }
}
